		function startup(){
			$("#name").val('');
			$("#residential_address").val('');
			$("#office_address").val('');
			$("#phone").val('');
			$("#gender").val('');
			$("#dob").val('');
			$("#origin").val('');
			$("#email").val('');
			$("#valid_id_detail").val('');
			$("#nature_of_business").val('');
			$("#bvn").val('');
			$("#bank_name").val('');
			$("#accountname").val('');
			$("#account_no").val('');
			$("#kin_name").val('');
			$("#kin_address").val('');
			$("#kin_phone").val('');
			$("#kin_relationship").val('');
			$("#savings_amount").val('');
			$("#frequency").val('');
		}
		
		$(document).ready(function() {
			$("#btnreset").click(function(){
				startup();
			});
			
			$("#btnsave").click(function(){
			   var name = $("#name").val().trim();
			   var residential_address = $("#residential_address").val().trim();
			   var office_address = $("#office_address").val().trim();
			   var phone = $("#phone").val().trim();
			   var gender = $("#gender").val().trim();
			   var dob = $("#dob").val().trim();
			   var origin = $("#origin").val().trim();
			   var email = $("#email").val().trim();
			   var valid_id_detail = $("#valid_id_detail").val().trim();
			  
			   var nature_of_business = $("#nature_of_business").val().trim();
			   var bvn = $("#bvn").val().trim();
			   var bank_name = $("#bank_name").val().trim();
			   var account_no = $("#account_no").val().trim();
			   var accountname = $("#accountname").val().trim();
			   var kin_name = $("#kin_name").val().trim();
			   var kin_address = $("#kin_address").val().trim();
			   var kin_phone = $("#kin_phone").val().trim();
			   var kin_relationship = $("#kin_relationship").val().trim();
			   var savings_amount = $("#savings_amount").val().trim();
			   var frequency = $("#frequency").val().trim();
			   if (account_no=='' || accountname=='' || name=='' || email=='' || phone=='' || residential_address=='' || office_address=='' || gender=='' || dob=='' || origin=='' || valid_id_detail=='' || nature_of_business=='' || bvn==''|| bank_name=='' 
					|| kin_name=='' || kin_address=='' || kin_phone=='' || kin_relationship=='' || savings_amount=='' || frequency==''){
				   var msg = "One or more inputs are empty or not correct";
				   alert(msg);				
				}else{
				   var frmValues = $("#frmobj").serialize()+ "&PT="+'BL';
				   $.post("managestudentloan.php",frmValues).done(function(data){
					   if (data==1){
						   alert("Detail Successfully submit!");
						   $("#accountno").val('');
						   startup();
						}else{
						   alert(data);
						}	
					});
				}
			});
		});
		
		function fetchAcctName() {
            const acctNo = document.getElementById('account_no').value

            if (acctNo.length < 10) return;

            $.get(`resolve-bank-names.php?account_number=${acctNo}&account_bank=${document.getElementById('bank_name').value}`, function(data, status){
                document.getElementById('accountname').value = data;
            });
        }
